import pytest

from src.LongestPalindromicSubstring.solution_top_down_approach import Solution


@pytest.mark.parametrize(
    argnames=["input_string", "expected"],
    argvalues=[
        ["babad", ["bab", "aba"]],
        ["cbbd", ["bb"]],
        ["babaddtattarrattatddetartrateedredividerb", ["ddtattarrattatdd"]],
        [
            "",
            ["qgjjgq"],
        ],
    ],
)
@pytest.mark.timeout(1)
@pytest.mark.limit_memory("100 KB")
def test_longestPalindrome(input_string, expected):
    actual = Solution().longestPalindrome(s=input_string)
    assert actual in expected
